package main

import (
	"testing"

	. "github.com/smartystreets/goconvey/convey"
)

func TestPart1(t *testing.T) {
	Convey("For the part1", t, func() {
		Convey("For demo inputs", func() {
			Convey("We get the right answer", func() {
				So(part1("bvwbjplbgvbhsrlpgdmjqwftvncz"), ShouldEqual, 5)
				So(part1("nppdvjthqldpwncqszvftbrmjlhg"), ShouldEqual, 6)
				So(part1("nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg"), ShouldEqual, 10)
				So(part1("zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw"), ShouldEqual, 11)
			})
		})
		Convey("For real input", func() {
			Convey("We get the right answer", func() {
				So(part1(""),
					ShouldEqual,
					1658)
			})
		})
	})
}

func TestPart2(t *testing.T) {
	Convey("For the part2", t, func() {
		Convey("For demo inputs", func() {
			Convey("We get the right answer", func() {
				So(part2("mjqjpqmgbljsphdztnvjfqwrcgsmlb"), ShouldEqual, 19)
				So(part2("bvwbjplbgvbhsrlpgdmjqwftvncz"), ShouldEqual, 23)
				So(part2("nppdvjthqldpwncqszvftbrmjlhg"), ShouldEqual, 23)
				So(part2("nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg"), ShouldEqual, 29)
				So(part2("zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw"), ShouldEqual, 26)
			})
		})
		Convey("For real input", func() {
			Convey("We get the right answer", func() {
				So(part2(""),
					ShouldEqual,
					2260) // 2261 is too high // 1980 is too low
			})
		})
	})
}
