package main

import (
	. "github.com/smartystreets/goconvey/convey"
	"testing"
)

func TestPart1(t *testing.T) {
	Convey("For the part1", t, func() {
		Convey("For demo inputs", func() {
			Convey("We get the right answer", func() {
				So(part1("bvwbjplbgvbhsrlpgdmjqwftvncz"), ShouldEqual, 5)
				So(part1("nppdvjthqldpwncqszvftbrmjlhg"), ShouldEqual, 6)
				So(part1("nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg"), ShouldEqual, 10)
				So(part1("zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw"), ShouldEqual, 11)
			})
		})
		Convey("For real input", func() {
			Convey("We get the right answer", func() {
				So(part1(""),
					ShouldEqual,
					1658)
			})
		})
	})
}

